mapper.select:
select s.*, m.name as majorName from students s left join majors m on s.major_id = m.id

    
        s.name like CONCAT('%', #{name}, '%')
    
    
        and s.major_id = #{majorId}

mapper.update:
update students
name = #{name}, student_id = #{studentId}, gender = #{gender}, age = #{age}, major_id = #{majorId}, score = #{score},
where id = #{id}
